document.getElementById("createWalletButton").addEventListener("click", function() {
    document.getElementById("createWalletSection").style.display = "block";
    document.getElementById("accessWalletSection").style.display = "none";
  });
  
  document.getElementById("generatePhraseButton").addEventListener("click", function() {
    const words = generateRecoveryPhrase();
    document.getElementById("recoveryPhrase").textContent = "Frase de Recuperação: " + words.join(" ");
    document.getElementById("recoveryPhrase").style.display = "block";
    document.getElementById("saveWalletButton").style.display = "inline-block";
  });
  
  function generateRecoveryPhrase() {
    const words = [
      "apple", "banana", "cat", "dog", "elephant", "fox", "grape", "house", "igloo", "joker", "kite", "lemon",
      "monkey", "night", "orange", "pear", "queen", "rain", "sun", "tree", "umbrella", "violin", "wolf", "xray", "yoyo", "zebra"
    ];
    const recoveryPhrase = [];
    for (let i = 0; i < 12; i++) {
      const randomIndex = Math.floor(Math.random() * words.length);
      recoveryPhrase.push(words[randomIndex]);
    }
    return recoveryPhrase;
  }
  